	/**
	 * set the values after verifying them
	 * @param type $values
	 * @return type
	 */
	function setValues($values){
		if($this->colCheck && !$this->checkAreColumns($values)) return false;

		$this->values=array();
		$this->values=$values;
		return true;
	}

	/**
	 *
	 * @param type $values
