    function get_prices(){
        $prices = array();
        $helper_toolbox = WYSIJA::get('toolbox' , 'helper');

        if($helper_toolbox->is_european()){
            $prices['blogger'] = '75€';
            $prices['freelancer'] = '189€';
            $prices['agency'] = '299€';
            $this->is_european = true;
        }else{
            $prices['blogger'] = '$99';
            $prices['freelancer'] = '$249';
            $prices['agency'] = '$399';
            $this->is_european = false;
        }
        return $prices;
    }
