			$thisfile_ac3_raw_bsi['surmixlev'] = $this->readHeaderBSI(2);
			$thisfile_ac3['surround_mix_level'] = self::surroundMixLevelLookup($thisfile_ac3_raw_bsi['surmixlev']);
